import { createRouter, createWebHistory } from 'vue-router';
import MainList from '../components/MainList.vue';
import IntroScreen from 'E:\SmartWay\Operacion_ByteStorm\OperacionFrontEnd2\bytestormfrontend\src\components\IntroScreen.vue'; // Ajustar el path si es necesario

const routes = [
  {
    path: '/', // P치gina inicial con la pantalla de bienvenida
    name: 'IntroScreen',
    component: IntroScreen,
  },
  {
    path: '/operativos', // P치gina principal
    name: 'MainList',
    component: MainList,
  },
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

export default router;
