export default {
  data() {
    return {
      equipos: []
    };
  },
  created() {
    this.fetchEquipos();
  },
  methods: {
    async fetchEquipos() {
      try {
        const response = await this.$axios.get('/api/equipo');
        this.equipos = response.data;
      } catch (error) {
        console.error(error);
      }
    }
  }
};
