import { defineStore } from 'pinia';
import api from './services/apiClient';

export const useMisionesStore = defineStore('misiones', {
  state: () => ({
    misiones: []
  }),
  actions: {
    async fetchMisiones() {
      try {
        const response = await api.getMisiones();
        this.misiones = response.data;
      } catch (error) {
        console.error("Error fetching misiones:", error);
      }
    },
    async addMision(mision) {
      try {
        const response = await api.addMision(mision);
        this.misiones.push(response.data);
      } catch (error) {
        console.error("Error adding mision:", error);
      }
    },
    async updateMision(id, updatedMision) {
      try {
        await api.updateMision(id, updatedMision);
        const index = this.misiones.findIndex(m => m.codigo === id);
        if (index !== -1) {
          this.misiones[index] = updatedMision;
        }
      } catch (error) {
        console.error("Error updating mision:", error);
      }
    },
    async deleteMision(id) {
      try {
        await api.deleteMision(id);
        this.misiones = this.misiones.filter(m => m.codigo !== id);
      } catch (error) {
        console.error("Error deleting mision:", error);
      }
    }
  }
});
