import { ref, watch, defineComponent } from "vue";
export default defineComponent({
  props: {
    equipoSeleccionado: {
      type: Object,
      default: null
    },
    operativoSeleccionado: {
      type: Object,
      default: null
    },
    misionSeleccionada: {
      type: Object,
      default: null
    }
  },
  setup(props, {
    emit
  }) {
    const formData = ref({
      tipo: "",
      id: "",
      nombre: "",
      rol: "",
      misionAsignada: "",
      Codigo: "",
      EquipoCodigo: "",
      descripcion: ""
    });
    const misiones = ref([]);

    // Watchers para actualizar el formData cuando cambian las selecciones
    watch(() => props.equipoSeleccionado, newVal => {
      if (newVal) {
        formData.value = {
          tipo: "Equipo",
          EquipoCodigo: newVal.EquipoCodigo || "",
          nombre: newVal.nombre || "",
          descripcion: newVal.descripcion || "",
          rol: "",
          misionAsignada: "",
          id: "",
          Codigo: ""
        };
      }
    }, {
      immediate: true
    });
    watch(() => props.operativoSeleccionado, newVal => {
      if (newVal) {
        formData.value = {
          tipo: "Operativo",
          id: newVal.id || "",
          nombre: newVal.nombre || "",
          rol: newVal.rol || "",
          misionAsignada: newVal.misionAsignada || "",
          EquipoCodigo: "",
          Codigo: "",
          descripcion: ""
        };
      }
    }, {
      immediate: true
    });
    watch(() => props.misionSeleccionada, newVal => {
      if (newVal) {
        formData.value = {
          tipo: "Misión",
          misionCodigo: newVal.misionCodigo || "",
          nombre: newVal.nombre || "",
          descripcion: newVal.descripcion || "",
          rol: "",
          misionAsignada: "",
          EquipoCodigo: "",
          id: ""
        };
      }
    }, {
      immediate: true
    });
    const submitForm = async () => {
      try {
        let apiUrl = "";
        let method = "POST";
        if (formData.value.tipo === "Operativo") {
          if (props.operativoSeleccionado) {
            apiUrl = `http://localhost:5056/api/operativo/${props.operativoSeleccionado.id}`;
            method = "PUT";
          } else {
            apiUrl = "http://localhost:5056/api/operativo/crear";
          }
        } else if (formData.value.tipo === "Mision") {
          if (props.misionSeleccionada) {
            apiUrl = `http://localhost:5056/api/mision/${props.misionSeleccionada.codigo}`;
            method = "PUT";
          } else {
            apiUrl = "http://localhost:5056/api/mision/crear";
          }
        } else if (formData.value.tipo === "Equipo") {
          if (props.equipoSeleccionado) {
            apiUrl = `http://localhost:5056/api/equipo/${props.equipoSeleccionado.EquipoCodigo}`;
            method = "PUT";
          } else {
            apiUrl = "http://localhost:5056/api/equipo/crear";
          }
        }
        const response = await fetch(apiUrl, {
          method: method,
          headers: {
            "Content-Type": "application/json"
          },
          body: JSON.stringify(formData.value)
        });
        if (!response.ok) {
          throw new Error("Error al guardar los datos");
        }
        emit("data-saved");
        resetForm();
      } catch (error) {
        console.error("Error al guardar los datos:", error);
      }
    };
    const resetForm = () => {
      formData.value = {
        tipo: "",
        operativoID: "",
        nombre: "",
        rol: "",
        misionAsignada: "",
        misionCodigo: "",
        EquipoCodigo: "",
        descripcion: ""
      };
    };
    return {
      formData,
      misiones,
      submitForm,
      resetForm
    };
  }
});
