import { reactive } from 'vue';
import { useRouter } from 'vue-router';

// Datos del formulario utilizando reactive

export default {
  __name: 'CrudForm',
  emits: ['data-saved'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const formData = reactive({
      nombre: '',
      descripcion: '',
      tipo: ''
    });

    // Obtener la función emit
    const emit = __emit;

    // Función para manejar el envío del formulario
    async function submitForm() {
      try {
        const response = await fetch('http://localhost:5056/api/crear', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(formData)
        });
        if (!response.ok) {
          throw new Error('Error al guardar los datos');
        }
        const result = await response.json();
        console.log('Datos guardados correctamente:', result);

        // Emitir un evento para que MainList.vue recargue los datos
        emit('data-saved');

        // Reiniciar el formulario
        formData.nombre = '';
        formData.descripcion = '';
        formData.tipo = '';
      } catch (error) {
        console.error('Error al guardar los datos:', error);
      }
    }
    const __returned__ = {
      formData,
      emit,
      submitForm,
      reactive,
      get useRouter() {
        return useRouter;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
