export default {
  props: {
    initialData: {
      type: Object,
      default: null
    }
  },
  data() {
    return {
      formData: {
        tipo: '',
        operativoID: '',
        nombre: '',
        rol: '',
        misionAsignada: '',
        misionCodigo: '',
        equipoID: ''
      },
      misiones: [],
      // Aquí puedes cargar las misiones disponibles
      isEditing: false
    };
  },
  watch: {
    initialData: {
      immediate: true,
      handler(newData) {
        if (newData) {
          this.isEditing = true;
          this.formData = {
            ...newData
          };
        }
      }
    }
  },
  methods: {
    async submitForm() {
      try {
        let apiUrl = '';
        if (this.formData.tipo === 'Operativo') {
          apiUrl = this.isEditing ? `http://localhost:5056/api/operativo/${this.formData.operativoID}` : 'http://localhost:5056/api/operativo/crear';
        } else if (this.formData.tipo === 'Misión') {
          apiUrl = this.isEditing ? `http://localhost:5056/api/mision/${this.formData.misionCodigo}` : 'http://localhost:5056/api/mision/crear';
        } else if (this.formData.tipo === 'Equipo') {
          apiUrl = this.isEditing ? `http://localhost:5056/api/equipo/${this.formData.equipoID}` : 'http://localhost:5056/api/equipo/crear';
        }
        const method = this.isEditing ? 'PUT' : 'POST';
        const response = await fetch(apiUrl, {
          method: method,
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(this.formData)
        });
        if (!response.ok) {
          throw new Error('Error al guardar los datos');
        }
        this.$emit('data-saved');
        this.resetForm();
      } catch (error) {
        console.error('Error al guardar los datos:', error);
      }
    },
    resetForm() {
      this.isEditing = false;
      this.formData = {
        tipo: '',
        operativoID: '',
        nombre: '',
        rol: '',
        misionAsignada: '',
        misionCodigo: '',
        equipoID: ''
      };
    }
  }
};
