export default {
  name: 'CrudForm',
  data() {
    return {
      formData: {
        nombre: '',
        descripcion: '',
        tipo: ''
      }
    };
  },
  methods: {
    async submitForm() {
      try {
        const response = await fetch('http://localhost:5056/api/crear', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify(this.formData) // Enviar los datos del formulario
        });
        if (!response.ok) {
          throw new Error('Error al guardar los datos');
        }
        const result = await response.json();
        console.log('Datos guardados correctamente:', result);

        // Emitir un evento para que MainList.vue recargue los datos
        this.$emit('data-saved');

        // Reiniciar el formulario
        this.formData = {
          nombre: '',
          descripcion: '',
          tipo: ''
        };
      } catch (error) {
        console.error('Error al guardar los datos:', error);
      }
    }
  }
};
