import { defineStore } from 'pinia';
import api from './services/apiClient';

export const useOperativosStore = defineStore('operativos', {
  state: () => ({
    operativos: []
  }),
  actions: {
    async fetchOperativos() {
      try {
        const response = await api.getOperativos();
        this.operativos = response.data;
      } catch (error) {
        console.error("Error fetching operativos:", error);
      }
    },
    async addOperativo(operativo) {
      try {
        const response = await api.addOperativo(operativo);
        this.operativos.push(response.data);
      } catch (error) {
        console.error("Error adding operativo:", error);
      }
    },
    async updateOperativo(id, updatedOperativo) {
      try {
        await api.updateOperativo(id, updatedOperativo);
        const index = this.operativos.findIndex(o => o.id === id);
        if (index !== -1) {
          this.operativos[index] = updatedOperativo;
        }
      } catch (error) {
        console.error("Error updating operativo:", error);
      }
    },
    async deleteOperativo(id) {
      try {
        await api.deleteOperativo(id);
        this.operativos = this.operativos.filter(o => o.id !== id);
      } catch (error) {
        console.error("Error deleting operativo:", error);
      }
    }
  }
});
