import { defineStore } from 'pinia';
import api from './services/apiClient';

export const useEquiposStore = defineStore('equipos', {
  state: () => ({
    equipos: []
  }),
  actions: {
    async fetchEquipos() {
      try {
        const response = await api.getEquipos();
        this.equipos = response.data;
      } catch (error) {
        console.error("Error fetching equipos:", error);
      }
    },
    async addEquipo(equipo) {
      try {
        const response = await api.addEquipo(equipo);
        this.equipos.push(response.data);
      } catch (error) {
        console.error("Error adding equipo:", error);
      }
    },
    async updateEquipo(id, updatedEquipo) {
      try {
        await api.updateEquipo(id, updatedEquipo);
        const index = this.equipos.findIndex(e => e.id === id);
        if (index !== -1) {
          this.equipos[index] = updatedEquipo;
        }
      } catch (error) {
        console.error("Error updating equipo:", error);
      }
    },
    async deleteEquipo(id) {
      try {
        await api.deleteEquipo(id);
        this.equipos = this.equipos.filter(e => e.id !== id);
      } catch (error) {
        console.error("Error deleting equipo:", error);
      }
    }
  }
});
