export default {
  data() {
    return {
      formData: {
        tipo: '',
        // 'Operativo', 'Misión', 'Equipo'
        operativoID: '',
        // Para Operativo
        nombre: '',
        rol: '',
        // Para Operativo
        misionAsignada: '',
        // Para Operativo
        misionCodigo: '',
        // Para Misión
        equipoID: '' // Para Equipo
      },
      misiones: [] // Aquí puedes cargar las misiones disponibles
    };
  },
  mounted() {
    this.loadMisiones(); // Cargar las misiones al montar el componente
  },
  methods: {
    async loadMisiones() {
      try {
        const response = await fetch('http://localhost:5056/api/mision'); // Ajusta la URL según corresponda
        if (!response.ok) {
          throw new Error('Error al cargar las misiones');
        }
        this.misiones = await response.json();
      } catch (error) {
        console.error('Error al cargar las misiones:', error);
      }
    },
    async submitForm() {
      try {
        let apiUrl = '';
        if (this.formData.tipo === 'Operativo') {
          apiUrl = 'http://localhost:5056/api/operativo/crear';
        } else if (this.formData.tipo === 'Misión') {
          apiUrl = 'http://localhost:5056/api/mision/crear';
        } else if (this.formData.tipo === 'Equipo') {
          apiUrl = 'http://localhost:5056/api/equipo/crear';
        }
        const response = await fetch(apiUrl, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            // Datos comunes
            nombre: this.formData.nombre,
            // Datos específicos según el tipo de entrada
            ...(this.formData.tipo === 'Operativo' && {
              operativoID: this.formData.operativoID,
              rol: this.formData.rol,
              misionAsignada: this.formData.misionAsignada
            }),
            ...(this.formData.tipo === 'Misión' && {
              misionCodigo: this.formData.misionCodigo
            }),
            ...(this.formData.tipo === 'Equipo' && {
              equipoID: this.formData.equipoID
            })
          })
        });
        if (!response.ok) {
          throw new Error('Error al guardar los datos');
        }
        this.$emit('data-saved');
        this.resetForm();
      } catch (error) {
        console.error('Error al guardar los datos:', error);
      }
    },
    resetForm() {
      this.formData = {
        tipo: '',
        operativoID: '',
        nombre: '',
        rol: '',
        misionAsignada: '',
        misionCodigo: '',
        equipoID: ''
      };
    }
  }
};
